import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { navigate, Link } from '@reach/router';
import {
    Paper,
    FormControl,
    InputLabel,
    OutlinedInput,
    Button
} from '@material-ui/core';
const styles = {
    paper: {
    width: "20rem", padding: "1rem"
    },
    input: {
    marginBottom: "1rem"
    },
    button: {
    width: "100%"
    }
}

export default () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [usernameError, setUsernameError] = useState('');

    const [profiles, setProfiles] = useState([]);
    const [loaded, setLoaded] = useState(false);
    useEffect(()=>{
        axios.get('http://localhost:8000/api/profile')
            .then(res=>{
                setProfiles(res.data);
                setLoaded(true);
            });
    },[])

    const onSubmitHandler = (e) => {
        e.preventDefault();
        for(var i = 0; i < profiles.length; i++) {
            if (profiles[i].email === username) {
                if(profiles[i].password === password) {
                    navigate('/feed');
                }
            }
        }
        setUsernameError("The username and password did not match our records. Please try again.");


    }     



    return (

        <Paper elevation={3} style={styles.paper}>
                    <h2>Login Form</h2>
                    <form>
                        <FormControl variant="outlined" style={styles.input}>
                            <InputLabel>Username</InputLabel>
                            <OutlinedInput type="text" onChange={(e) => setUsername(e.target.value)} />
                        </FormControl>
                        <FormControl variant="outlined" style={styles.input}>
                            <InputLabel>Password</InputLabel>
                            <OutlinedInput type="password" onChange={(e) => setPassword(e.target.value)} />
                        </FormControl>
                        <Button type="submit" variant="contained" color="primary" onSubmit={onSubmitHandler}>
                            Log In
                        </Button>
                        <Link to='/register'>Register</Link>
                    </form>
                    <p>{profiles[0]}</p>
        </Paper>
    )
}
